import React, { useContext, useEffect } from 'react';
import noteContext from '../context/notes/noteContext';
const context=useContext(noteContext);
useEffect(() => {
  if (localStorage.getItem('token')){
    getNotes()
  }
 

}, [])

let { getNotes,sjson} = context;
const [likeCount, setLikeCount] = useState(0);
const [liked, setLiked] = useState(false);

const handleLike = () => {
  if(!liked){
  setLikeCount(likeCount + 1);
  setLiked(true);
  }
};

const FeedNote = ({ note }) => {
  
  return (
    
        

 

  
 

    <div className="card my-3" style={{ width: "18rem" }} >
        <div className="card-body">
            <div className='d-flex align-items-center'>
                <h5 className="card-title" style={{ color: "#333", marginBottom: "10px" }}>{sjson.notes.title}</h5>


            

            </div>
            <div className="card-text" style={{ fontSize: "14px", color: "#555" }}>{sjson.notes.description}</div>   
         

            <div className="d-flex align-items-center mt-3">
            <button className="btn btn-primary btn-sm me-2" onClick={handleLike}  disabled={liked}>
                {liked ? "Liked" : "Like"}
            </button>
            <span>{likeCount} Like</span>
            </div>
      

        </div>
        </div> 
   



  


  
   )
 
};

const Feeeeed = () => {
  const context = useContext(noteContext);
  const { getNotes, sjson } = context;

  useEffect(() => {
    if (localStorage.getItem('token')) {
      getNotes();
    }
  }, []);

  return (
    <div>
      {sjson.map((note) => (
        <FeedNote key={note.id} note={note.notes} />
      ))}
    </div>
  );
};

export default Feeeeed;
