// export default SearchBar

import React, { useContext, useState, useEffect } from 'react';
import noteContext from '../context/notes/noteContext';

const SearchBar = () => {
  const context = useContext(noteContext);
  const { key, notes } = context;
  const [storedOutput, setStoredOutput] = useState(null);

  useEffect(() => {
    const storedData = localStorage.getItem('searchBarOutput');
    if (storedData) {
      setStoredOutput(JSON.parse(storedData));
    }
  }, []);

  useEffect(() => {
    if (storedOutput) {
      localStorage.setItem('searchBarOutput', JSON.stringify(storedOutput));
    }
  }, [storedOutput]);

  return (
    <div>
      {storedOutput || (
        <div>
          {notes.map((note) => {
            if (note._id === key) {
              return (
                <div className='col-md-3' key={note._id}>
                  <div className="card my-3">
                    <div className="card-body">
                      <div className='d-flex align-items-center'>
                        <h5 className="card-title">{note.title}</h5>
                      </div>
                      <div className="card-text">{note.description}</div>
                    </div>
                  </div>
                </div>
              );
            }
          })}
        </div>
      )}
    </div>
  );
};

export default SearchBar;
