    import React, { useState } from 'react';

    const BlogPost = ({ post }) => {
      const [likes, setLikes] = useState(post.likes);
      const [reactions, setReactions] = useState(post.reactions);
    
      const handleLike = () => {
        setLikes(likes + 1);
      };
    
      const handleReaction = (reaction) => {
        const updatedReactions = { ...reactions };
        updatedReactions[reaction] = updatedReactions[reaction] + 1 || 1;
        setReactions(updatedReactions);
      };
    
      return (
        <div className="blog-post">
          <h2>{post.title}</h2>
          <p>{post.content}</p>
          <div className="social">
            <button onClick={handleLike}>Like</button>
            <span>{likes} likes</span>
            <div className="reactions">
              <button onClick={() => handleReaction('heart')}>‚ù§Ô∏è</button>
              <span>{reactions.heart || 0}</span>
              <button onClick={() => handleReaction('thumbsUp')}>üëç</button>
              <span>{reactions.thumbsUp || 0}</span>
              <button onClick={() => handleReaction('clap')}>üëè</button>
              <span>{reactions.clap || 0}</span>
            </div>
          </div>
        </div>
      );
    };
    
    export default BlogPost;
    

