import React from 'react'
import { useContext,useEffect } from 'react'
import noteContext from '../context/notes/noteContext'

const About = () => {
    const a = useContext(noteContext)
    useEffect(() => {
        a.update(); 
    }, []);
  return (
    <div> This is About{a.state.name}</div>
  )
}

export default About
