import React,{useContext, useState,useEffect}from 'react'
import noteContext from '../context/notes/noteContext';
import FeedNote from './FeedNote';





// const Feeeeed = () => {
  
    
    



   
    
    
//   return (
  
// <div>

// <FeedNote/>


 
// </div>

//   )
// }

// export default Feeeeed

const Feeeeed = () => {
  const context = useContext(noteContext);
  const { getNotes, sjson } = context;
  const [searchInput, setSearchInput] = useState('');
  const [filteredNotes, setFilteredNotes] = useState([]);

  useEffect(() => {
    if (localStorage.getItem('token')) {
      getNotes();
    }
  }, []);

  useEffect(() => {
    if (searchInput === '') {
      setFilteredNotes(sjson);
    } else {
      const filtered = sjson.filter((note) =>
        note.notes.tag.toLowerCase() === searchInput.toLowerCase()
      );
      setFilteredNotes(filtered);
    }
  }, [searchInput, sjson]);

  return (
    <div>
      <input
        type="text"
        value={searchInput}
        onChange={(e) => setSearchInput(e.target.value)}
        placeholder="Search by tag..."
      />

      {filteredNotes.map((note) => (
        <FeedNote key={note.id} note={note.notes} />
      ))}
    </div>
  );
};

export default Feeeeed;
