import React, { useContext, useState, useEffect } from 'react';
import noteContext from '../context/notes/noteContext';

const FeedNote = ({ note }) => {
  const [likeCount, setLikeCount] = useState(0);
  const [liked, setLiked] = useState(false);

  const handleLike = () => {
    if (!liked) {
      setLikeCount(likeCount + 1);
      setLiked(true);
    }
  };

  return (
    <div className="card my-3" style={{ width: "18rem" }}>
      <div className="card-body">
        <div className="d-flex align-items-center">
          <h5 className="card-title" style={{ color: "#333", marginBottom: "10px" }}>{note.title}</h5>
        </div>
        <div className="card-text" style={{ fontSize: "14px", color: "#555" }}>{note.description}</div>
        <div className="d-flex align-items-center mt-3">
          <button className="btn btn-primary btn-sm me-2" onClick={handleLike} disabled={liked}>
            {liked ? "Liked" : "Like"}
          </button>
          <span>{likeCount} Like</span>
        </div>
      </div>
    </div>
  );
};

const Feeeeed = () => {
  const context = useContext(noteContext);
  const { getNotes, sjson } = context;
  const [searchInput, setSearchInput] = useState('');
  const [filteredNotes, setFilteredNotes] = useState([]);

  useEffect(() => {
    if (localStorage.getItem('token')) {
      getNotes();
    }
  }, []);

  useEffect(() => {
    if (searchInput === '') {
      setFilteredNotes(sjson);
    } else {
      const filtered = sjson.filter((note) =>
        note.notes.title.toLowerCase().includes(searchInput.toLowerCase())
      );
      setFilteredNotes(filtered);
    }
  }, [searchInput, sjson]);

  return (
    <div>
      <input
        type="text"
        value={searchInput}
        onChange={(e) => setSearchInput(e.target.value)}
        placeholder="Search by title..."
      />

      {filteredNotes.map((note) => (
        <FeedNote key={note.id} note={note.notes} />
      ))}
    </div>
  );
};

export default Feeeeed;
